export const actionTypes = {
  getCocktailData: 'GET_COCKTAIL_DATA',
  loading: 'LOADING',
  error: 'ERROR',
};

const appReducer = (state, action) => {
  const { type, payload } = action;

  switch (type) {
    case actionTypes.getCocktailData:
      return {
        ...state,
        cocktailData: payload,
      };
    case actionTypes.loading:
      return {
        ...state,
        isLoading: payload,
      };
    case actionTypes.error:
      return {
        ...state,
        isError: payload,
      };

    default:
      return state;
  }
};

export default appReducer;
