import React, {
  useState,
  useContext,
  useEffect,
  createContext,
  useReducer,
} from 'react';
import { useCallback } from 'react';
import appReducer from './appReducer';
import { useFetch } from './hooks/useFetch';

const url = '/api/json/v1/1/search.php?s=';

const initialState = {
  cocktailData: [],
};

export const AppContext = createContext({
  cocktailData: [],
});

export const AppProvider = () => {
  const { data, isLoading, isError } = useFetch(url);
  if (!isLoading) {
    console.log(data);
  }
  const [{ cocktailData }, dispatch] = useReducer(appReducer, initialState);

  const value = {
    cocktailData,
  };
  return <AppContext.Provider value={value}></AppContext.Provider>;
};
