import React from 'react';
import Cocktail from './Cocktail';
import Loading from './Loading';
import { useGlobalContext } from '../context';

const CocktailList = ({ data }) => {
  return (
    <div>
      {data.map((cocktail) => {
        const {
          idDrink,
          strDrink,
          strGlass,
          strAlchholic,
          strDrinkThumb,
          strInstructions,
        } = cocktail;

        return (
          <div key={idDrink}>
            <img className="img" src={strDrinkThumb} alt={strDrink} />
            <h3>{strDrink}</h3>
            <p>{strGlass}</p>
            <p>{strAlchholic}</p>
          </div>
        );
      }) ?? <h4>Drink does not exist</h4>}
    </div>
  );
};

export default CocktailList;
