import React, {
  useState,
  useContext,
  useEffect,
  createContext,
  useReducer,
} from 'react';

import appReducer, { actionTypes } from './appReducer';
import { useFetch } from './hooks/useFetch';

const initialState = {
  cocktailData: [],
  isLoading: true,
  isError: false,
};
const url = 'https://www.thecocktaildb.com/api/json/v1/1/search.php?s=';
export const AppContext = createContext();

export const AppProvider = ({ children }) => {
  const [{ cocktailData }, dispatch] = useReducer(appReducer, initialState);
  const { data, isLoading, isError } = useFetch(url);

  const dispatchHandler = (type, payload) => {
    dispatch({
      type,
      payload,
    });
  };

  useEffect(() => {
    dispatchHandler(actionTypes.getCocktailData, data);
  }, [data]);
  useEffect(() => {
    dispatchHandler(actionTypes.loading, isLoading);
  }, [isLoading]);
  useEffect(() => {
    dispatchHandler(actionTypes.error, isError);
  }, [isError]);

  const value = {
    cocktailData,
    data,
    isLoading,
    isError,
  };
  return <AppContext.Provider value={value}>{children}</AppContext.Provider>;
};
