import React from 'react';
import Cocktail from './Cocktail';
import Loading from './Loading';
import { useGlobalContext } from '../context';

const CocktailList = ({ data }) => {
  return (
    <div>
      {data.map((cocktail) => {
        const {
          idDrink,
          strDrink,
          strGlass,
          strAlcoholic,
          strDrinkThumb,
          strInstructions,
        } = cocktail;

        return (
          <div className="cocktails-center" key={idDrink}>
            <img className="img" src={strDrinkThumb} alt={strDrink} />
            <h3>{strDrink}</h3>
            <p>{strGlass}</p>
            <p>{strAlcoholic}</p>
          </div>
        );
      })}
    </div>
  );
};

export default CocktailList;
